        return 0
    m, n = len(grid), len(grid[0])
    dirs = ((1,0),(-1,0),(0,1),(0,-1))
    islands = 0

    for i in range(m):
        for j in range(n):
            if grid[i][j] == "1":
                islands += 1
                grid[i][j] = "0" # mark as visited
                q = deque([(i, j)])
                while q:
